import React from 'react';
import axios from 'axios';
import {
    InputGroup, InputGroupAddon, Input//, InputGroupText
} from 'reactstrap';


import {
    Card, CardImg, CardText, CardBody,
    CardTitle, Button, Container, Row, Col
} from 'reactstrap';

export default class BoxesList extends React.Component {

    state = {
        boxes: [],
        currentImage: 0,
        viewerIsOpen: false,
        images: [],
        searchBox: ''
    };

    handleChange = event => {
        this.setState({ searchBox: event.target.value });
    }

    handleKeyDown = event => {
        if(event.keyCode ===  13) {
            this.fetchResult;
        }
    }

    fetchResult = event => {
        alert(this.state.searchBox);
    }


    toggleModal = () => {
        this.setState(state => ({ modalIsOpen: !state.modalIsOpen }));
    }

    componentDidMount() {
        axios.get("https://kpkzrk2tc7.execute-api.us-east-1.amazonaws.com/dev/boxes/").then(res => {
            this.setState({ images: res.data.items })
        });
    }




    render() {
        return (

            <div class="row justify-content-center">
                <br /><br /><br />

                <div className='col-cm-12'>
                    <InputGroup>
                        <InputGroupAddon addonType="prepend">
                            {/* <InputGroupText>
                                <span>M</span>
                                <Input addon type="checkbox" aria-label="Checkbox for following text input" />
                            </InputGroupText> */}
                        </InputGroupAddon>
                        <Input onKeyDown={this.handleKeyDown} onChange={this.handleChange} placeholder="Search Box #" />
                        <button onClick={this.fetchResult}>Search</button>
                    </InputGroup>
                </div>

                <br /><br /><br />

                <Container className="themed-container">
                    <Row>
                        {this.state.images.map(box => (
                            <Col sm='4'>
                                <Card>
                                    <CardImg top src={box.attachmentUrl} />
                                    <CardBody>
                                        <CardTitle>Box #: {box.sku} </CardTitle>
                                        <CardText>
                                            W: {box.width} MM => {box.width / 10} CM <br />
                                            L: {box.length || box.leng} MM => {(box.length || box.leng) / 10} CM <br />
                                            H: {box.height} MM => {box.height / 10} CM
                                        </CardText>
                                        <Button>Button</Button>
                                    </CardBody>
                                </Card>
                            </Col>
                        ))
                        }
                    </Row>
                </Container>

            </div>
        )
    }

}
